import React, { useState, useEffect } from 'react';
import './App.scss';
import { fetchTrendingGifs, searchGifs } from './services/api';
import GifList from './components/GifList';
import SearchBar from './components/SearchBar';
import Pagination from './components/Pagination';

function App() {
  const [gifs, setGifs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10); // Number of items per page
  const [searchQuery, setSearchQuery] = useState('');
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    if (searchQuery === '') {
      fetchTrending();
    } else {
      performSearch(searchQuery);
    }
  }, [currentPage, searchQuery]);

  const fetchTrending = async () => {
    const data = await fetchTrendingGifs(itemsPerPage, (currentPage - 1) * itemsPerPage);
    setGifs(data);
    // Calculate total pages based on total count from API
    setTotalPages(Math.ceil(data.pagination.total_count / itemsPerPage));
  };

  const performSearch = async (query) => {
    const data = await searchGifs(query, itemsPerPage, (currentPage - 1) * itemsPerPage);
    setGifs(data);
    // Calculate total pages based on total count from API
    setTotalPages(Math.ceil(data.pagination.total_count / itemsPerPage));
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1);
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="App">
      <h1>Giphy GIF Viewer</h1>
      <SearchBar onSearch={handleSearch} />
      <GifList gifs={gifs} />
      {totalPages > 1 && (
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      )}
    </div>
  );
}

export default App;
