import React from 'react';

function SearchBar({ query, onQueryChange, onSearch }) {
  return (
    <div>
      <input
        type="text"
        placeholder="Search for GIFs"
        value={query}
        onChange={onQueryChange}
      />
      <button onClick={onSearch}>Search</button>
    </div>
  );
}

export default SearchBar;
