import React, { useState, useEffect } from 'react';
import './App.scss';
import { fetchTrendingGifs, searchGifs } from './services/api';
import GifList from './components/GifList';
import SearchBar from './components/SearchBar';
import Pagination from './components/Pagination';

function App() {
  const [gifs, setGifs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10); // Number of items per page
  const [searchQuery, setSearchQuery] = useState('');
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    if (searchQuery === '') {
      fetchTrending();
    } else {
      performSearch(searchQuery);
    }
  }, [currentPage, searchQuery]);

  const fetchTrending = async () => {
    try {
      const data = await fetchTrendingGifs(itemsPerPage, (currentPage - 1) * itemsPerPage);
      setGifs(data.data); // Update with the actual data from the API
      setTotalPages(data.pagination.total_count ? Math.ceil(data.pagination.total_count / itemsPerPage) : 0);
    } catch (error) {
      console.error('Error fetching trending GIFs:', error);
    }
  };

  const performSearch = async (query) => {
    try {
      const data = await searchGifs(query, itemsPerPage, (currentPage - 1) * itemsPerPage);
      setGifs(data.data); // Update with the actual data from the API
      setTotalPages(data.pagination.total_count ? Math.ceil(data.pagination.total_count / itemsPerPage) : 0);
    } catch (error) {
      console.error('Error performing search:', error);
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1);
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="App">
      <h1>Giphy GIF Viewer</h1>
      <SearchBar onSearch={handleSearch} />
      <GifList gifs={gifs} />
      {totalPages > 1 && (
        <Pagination
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      )}
    </div>
  );
}

export default App;
